def convert2gray(img):
    """
    图片转为灰度图，如果是3通道图则计算，单通道图则直接返回
    :param img:
    :return:
    """
    if len(img.shape) > 2:
        r, g, b = img[:, :, 0], img[:, :, 1], img[:, :, 2]
        gray = 0.2989 * r + 0.5870 * g + 0.1140 * b
        return gray
    else:
        return img


def test_with_owen_img(img):
    # img = Image.open("D:\\个人研究\\chat_robots\\b_mnist\\自制手写图\\2_2.jpg")
    img_array = np.array(img)
    gray = convert2gray(img_array)
    test_img = gray.flatten() / 255
    recognize_recognize = recognize.recognize([test_img])
